fun factorial(num : Int): Int{
    if(num == 0) return 1
    return num * factorial(num-1)
}

fun main(){

    var intList = intArrayOf(1,2,3,7,7,7,90)
    var que: Queue<Int>  = LinkedList<Int>();
    for(i in 0..10){
        que.add(i)
    }

    println(que.remove())
    que.remove()
    que.remove()
    que.remove()
    que.remove()
    sayHello("pew", que)

    println(factorial(5))

    sayHelloInt("pew", intList)
}